import React from 'react';
import styled from 'styled-components';

const Wrapper = styled.div`
  width: 816px;
  margin: 0px 102px 30px 78px;
  text-align: left;

  display: flex;

  .commentProfile {
    width: 50px;
    height: 50px;
  }

  .commentContainer { /* 회색 말풍선 박스 */
    width: 758px;
    display: inline-block;
    border-radius: 10px;
    margin-left: 8px;
    background: #F6F6F6;
    font-family: Roboto;
    padding: 20px 25px 25px 25px;

    /* 여기서부터는 말풍선 박스 내부 */
    .commentWriter { // 댓글 작성자
      font-weight: bold;
      font-size: 20px;
      line-height: 23px;
      margin-right: 5px;
    }
    .commentDate  { // 댓글 등록일
      font-weight: 500;
      font-size: 14px;
      line-height: 16px;
      color: #959595;
    }
    .commentUpdate { // 댓글 수정
      font-weight: 500;
      font-size: 14px;
      line-height: 16px;
      color: #959595;

      margin-left: 500px; /* 임시 레이아웃 */
      cursor: pointer;
    }
    .commentDelete { // 댓글 삭제
      font-weight: 500;
      font-size: 14px;
      line-height: 16px;
      color: #FF0000;

      margin-left: 20px;
      cursor: pointer;
    }
  }
`;

const Comment = (props) => {
  return <>
    <Wrapper>
      <img className="commentProfile" src="assets/images/icons/profile.png" alt="profile" />
      <div className="commentContainer">
        <span className="commentWriter">작성자</span>
        <span className="commentDate">2시간 전</span>
        <span className="commentUpdate">수정</span>
        <span className="commentDelete">삭제</span>
        <p className="commentContents">
          
        </p>
      </div>
    </Wrapper>
  </>;
};

export default Comment;
