import React from 'react';
import styled from 'styled-components';
import Comment from './Comment';

const Wrapper = styled.section`

  .commentsStart {
    font-family: Roboto;
    font-style: normal;
    font-weight: 500;
    font-size: 22px;
    line-height: 26px;

    text-align: left;
    margin-bottom: 15px;
  }

  .commentsBox {
    border: 1px solid #FEA82F;
    box-sizing: border-box;
    border-radius: 10px;
  }

  .commentsInput {
    border: 1px solid #C4C4C4;
    border-radius: 100px;
    text-align: left;
    margin: 40px 78px 30px 78px;

    font-family: Roboto;
    font-style: normal;
    font-weight: normal;
    font-size: 16px;
    line-height: 19px;
  }

  .commentsProfile {
    margin: 11px 14px 11px 11px;
  }

  .commentsInput > input { /* 이름이 생각나지 않아서 자식 태그 선택자로 정함. */
    width: 700px;

    border: none;
    outline: none;
  }

  .commentsInput > span { /* 이름이 생각나지 않아서 자식 태그 선택자로 정함. */
    color: #F98404;
  }
`;

const Comments = (props) => {
  return <>
    <Wrapper>
      <h2 className="commentsStart">
        <img src="assets/images/icons/comments.png" alt="icon" />  이 독서모임 댓글 (50+)
      </h2>
      <div className="commentsBox">
        <div className="commentsInput">
          <img className="commentsProfile" src="assets/images/icons/profile.png" alt="profile" />
          <input type="text" placeholder="댓글을 입력하세요..." />
          <span>개시</span>
        </div>
        <Comment />
        <p>댓글</p>
      </div>
    </Wrapper>
  </>;
};

export default Comments;
