import React from "react";
import styled from "styled-components";
import { Card } from "antd";
import Tags from "./tags";

const { Meta } = Card;

const Wrapper = styled(Card)`
  width: 346px;
  height: 320px;
  border: 1px solid;
  border-radius: 10px;

/* ant-desing 코드 ~~ */

  .ant-card-meta-title {
		font-family: Roboto;
		font-weight: bold;
		font-size: 20px;
		line-height: 36px;
	}
	.ant-card-meta-description {
		font-family: Roboto;
		font-size: 16px;
		line-height: 36px;
		color: black;
	}

  /* ~~ ant-desing 코드 */

  .container {
		display: flex;
		justify-content: space-between;
		align-items: flex-end;
  }

	.like {
		display: flex;
		flex-direction: column;
		align-items: center;
	}
`;

const GatherCards = (props) => {
  return <>
    <Wrapper
      hoverable
        cover={
          <img src="assets/images/gatherCardThumbnail.png" alt="images" />
        }
    >
      <Meta title="독서 모임 이름" description="한 줄 소개" />
      <div className="container">
        <span className="tags">
          <Tags />
          <Tags />
        </span>
        <div className="like">
          <img className="likeButton" src="assets/images/like.png" alt="icon" />
          <span>9,999</span>
        </div>
      </div>
    </Wrapper>
  </>
};

export default GatherCards;
